mapper.update:
UPDATE tbl_servicio_dato_srdt SET srdt_cadena = ( WITH sql AS ( SELECT COALESCE( ( SELECT ssdt_cadena FROM tbl_subservicio_dato_ssdt WHERE ssdt_ssrv_pk = ssrv_pk AND ssdt_tpdt_pk = getTipoDato('TIPO_ESTAN_ATR_3') ) , ( SELECT ssdt_cadena FROM tbl_subservicio_dato_ssdt WHERE ssdt_ssrv_pk = ssrv_pk AND ssdt_tpdt_pk = getTipoDato('TIPO_ESTAN_ATR_2') ) , ( SELECT ssdt_cadena FROM tbl_subservicio_dato_ssdt WHERE ssdt_ssrv_pk = ssrv_pk AND ssdt_tpdt_pk = getTipoDato('TIPO_ESTAN_ATR') ) ) AS ssdt_cadena FROM tbl_subservicio_ssrv WHERE ssrv_srvc_pk = srdt_srvc_pk AND ssrv_tpss_pk = getEntidad('ATRAQUE') ) SELECT CASE WHEN 'C' = ALL (SELECT ssdt_cadena FROM sql) THEN 'C'
WHEN 'L' = aLL (SELECT ssdt_cadena FROM sql) THEN 'L'
ELSE 'M' END ) WHERE srdt_tpdt_pk = getTipoDato('TIPO_ESTAN_ESC') AND srdt_srvc_pk = #{value}

UPDATE tbl_servicio_dato_srdt srdtIva SET srdt_prmt_pk = ( SELECT prmt_pk FROM tbl_parametro_prmt WHERE prmt_tppr_pk = getEntidad('TIPO_IVA') AND prmt_parametro = ( WITH sql AS ( SELECT srdt_prmt_pk AS navt_ent_pk , srvc_fref , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = getTipoDato('TIPO_NAVEGACION_2') ) AS navt_sal_pk , ( SELECT srdt_nentero FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = getTipoDato('BOOLEANO_02') ) AS nav_int , ( SELECT prmt_parametro FROM tbl_parametro_prmt WHERE EXISTS ( SELECT prdt_prmt_pk FROM tbl_parametro_dato_prdt WHERE exists ( select 1 from tbl_parametro_version_prvr WHERE prvr_pk = prdt_prvr_pk AND prvr_prmt_pk = srdt_prmt_pk AND srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref) ) AND prdt_prmt_pk = prmt_pk AND prdt_tpdt_pk = getTipoDato('TIPO_IVA') ) ) AS ivat FROM tbl_servicio_dato_srdt srdtNavEnt join tbl_servicio_srvc ON srvc_pk = srdt_srvc_pk WHERE srdtNavEnt.srdt_tpdt_pk = getTipoDato('TIPO_NAVEGACION') AND srdtNavEnt.srdt_srvc_pk = srdtIva.srdt_srvc_pk ) SELECT CASE WHEN nav_int = 1 THEN 'X'
WHEN navt_ent_pk = navt_sal_pk THEN ivat
ELSE 'G' END FROM sql ) ) WHERE srdt_tpdt_pk = getTipoDato('TIPO_IVA') AND srdt_srvc_pk = #{value}
