mapper.select:
SELECT vlrc_pk, vlrc_fref , prmt_pk AS vlri_pk , prmt_parametro AS vlri_parametro , prmt_tppr_pk AS vlri_tppr_pk , prvr_pk AS vlri_prvr_pk , vlri_porcentaje , SUM(vlri_importe_base) AS vlri_importe_base , SUM(vlri_importe_base) * vlri_porcentaje / 100 AS vlri_importe_impuesto
, ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_pref = 'prvr' AND i18n_lang = #{idioma} AND i18n_ext_pk = prvr_pk ) AS vlri_descripcion
FROM ( SELECT vlrc_pk, vlrc_fref , prmt_pk, prmt_parametro, prmt_tppr_pk , prvr_pk , ( SELECT prdt_ndecimal FROM tbl_parametro_dato_prdt WHERE prdt_tpdt_pk = portico.getTipoDato('DECIMAL_01') AND prdt_prvr_pk = prvr_pk ) AS vlri_porcentaje , ( SELECT SUM(vlrd_importe) FROM tbl_valoracion_det_vlrd WHERE vlrd_vlrl_pk = vlrl.vlrl_pk ) AS vlri_importe_base FROM tbl_valoracion_vlrc INNER JOIN tbl_valoracion_lin_vlrl vlrl ON vlrl.vlrl_vlrc_pk = vlrc_pk INNER JOIN tbl_valoracion_lin_vlrl vlrlPadre ON vlrlPadre.vlrl_pk = vlrl.vlrl_padre_pk INNER JOIN tbl_parametro_prmt ON prmt_pk = vlrlPadre.vlrl_impuesto_prmt_pk LEFT JOIN tbl_parametro_version_prvr ON prvr_prmt_pk = vlrlPadre.vlrl_impuesto_prmt_pk AND vlrc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, vlrc_fref)
AND vlrc_pk = #{id}
AND vlrc_pk IN #{item}
) GROUP BY vlrc_pk, vlrc_fref , prmt_pk, prmt_parametro, prmt_tppr_pk , prvr_pk, vlri_porcentaje
