mapper.select:
SELECT ssdt_ssrv_pk, ssdt_tpdt_pk, ssdt_nentero, ssdt_ndecimal, ssdt_fecha, ssdt_cadena, ssdt_prmt_pk , prmt_parametro, prmt_prto_pk
, ( CASE WHEN EXISTS ( SELECT 1 FROM tbl_tipo_parametro_tppr WHERE tppr_pk = prmt_tppr_pk AND tppr_es_i18n = 1 ) THEN ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_lang = #{idioma} AND i18n_pref = 'prvr' AND i18n_ext_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = ssdt_prmt_pk AND EXISTS ( SELECT 1 FROM tbl_servicio_srvc WHERE prvr_fini <= srvc_fref AND (prvr_ffin IS NULL OR prvr_ffin > srvc_fref) AND srvc_pk = ssrv_srvc_pk
) ) ) ELSE ( SELECT prdt_cadena FROM tbl_parametro_dato_prdt WHERE prdt_tpdt_pk = ( SELECT tppr_tpdt_pk FROM tbl_tipo_parametro_tppr WHERE tppr_pk = prmt_tppr_pk ) AND prdt_prvr_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = ssdt_prmt_pk AND EXISTS ( SELECT 1 FROM tbl_servicio_srvc WHERE prvr_fini <= srvc_fref AND (prvr_ffin IS NULL OR prvr_ffin > srvc_fref) AND srvc_pk = ssrv_srvc_pk
) ) ) END ) AS p18n_texto
FROM tbl_subservicio_dato_ssdt INNER JOIN tbl_subservicio_ssrv ON ssrv_pk = ssdt_ssrv_pk LEFT JOIN tbl_parametro_prmt ON prmt_pk = ssdt_prmt_pk

mapper.insert:

INSERT INTO tbl_subservicio_dato_ssdt (
    ssdt_ssrv_pk, ssdt_tpdt_pk, ssdt_nentero, ssdt_ndecimal
    , ssdt_fecha, ssdt_prmt_pk, ssdt_cadena
) VALUES (
    #{itemId}, #{tpdtId}, #{cantidadEntera}, #{cantidadDecimal}
    , #{fecha}, #{prmt.id}, #{cadena}
)

mapper.update:

UPDATE tbl_subservicio_dato_ssdt SET
    ssdt_nentero = #{cantidadEntera}
    , ssdt_ndecimal = #{cantidadDecimal}
    , ssdt_fecha = #{fecha}
    , ssdt_prmt_pk = #{prmt.id}
    , ssdt_cadena = #{cadena}
WHERE
    ssdt_ssrv_pk = #{itemId}
    AND ssdt_tpdt_pk = #{tpdtId}
