mapper.select:

    SELECT COUNT(1)
    FROM
        tbl_i18n_i18n SET
    WHERE
        i18n_pref = #{prefix}
        AND i18n_ext_pk = #{externalId}
        AND i18n_lang = #{language}

mapper.update:

    UPDATE tbl_i18n_i18n SET
        i18n_text = #{text}
    WHERE
        i18n_pref = #{prefix}
        AND i18n_ext_pk = #{externalId}
        AND i18n_lang = #{language}

mapper.delete:

    DELETE FROM tbl_i18n_i18n
    WHERE
        i18n_pref = #{prefix}
        AND i18n_ext_pk = #{externalId}
        AND i18n_lang = #{language}
