mapper.sql:
        
            select
                srdt_srvc_pk, srdt_tpdt_pk, srdt_nentero, srdt_ndecimal, srdt_fecha, srdt_prmt_pk, srdt_srvc_dep_pk, srdt_cadena
                , prmt_parametro
                , prvr_pk
                , srvc_subp_pk, srvc_anno, srvc_numero
                , srvc_subp
        
        
        
                , (CASE
                    WHEN tppr_es_i18n = 1
                    THEN (
                        SELECT p18n_texto
                        FROM tbl_parametro_i18n_p18n
                        WHERE p18n_prvr_pk = prvr_pk AND p18n_idioma = #{idioma}
                    )
                    WHEN tppr_tpdt_nombre_pk IS NOT NULL
                    THEN (
                        SELECT prdt_cadena
                        FROM tbl_parametro_dato_prdt
                        WHERE prdt_prvr_pk = prvr_pk AND prdt_tpdt_pk = tppr_tpdt_nombre_pk
                    )
                END
                ) as prmt_texto
        
        
        
            from (
                SELECT
                    srdt_srvc_pk, srdt_tpdt_pk, srdt_nentero, srdt_ndecimal, srdt_fecha, srdt_prmt_pk, srdt_srvc_dep_pk, srdt_cadena
                    , prmt_parametro
                    , tppr_es_i18n, tppr_tpdt_nombre_pk
                    , (SELECT prvr_pk FROM tbl_parametro_version_prvr
                        WHERE
                        prvr_prmt_pk = srdt_prmt_pk
                        AND EXISTS (
                            SELECT 1 FROM tbl_servicio_srvc
                            WHERE srvc_pk = srdt_srvc_pk
                                AND srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref)
                        )
                    ) AS prvr_pk
                    , srvc_subp_pk, srvc_anno, srvc_numero
                    , (
                        SELECT prmt_parametro FROM tbl_parametro_prmt
                        WHERE prmt_pk = srvc_subp_pk
                    ) AS srvc_subp
                FROM tbl_servicio_dato_srdt
                    LEFT JOIN tbl_parametro_prmt ON
                        prmt_pk = srdt_prmt_pk
                    LEFT JOIN tbl_tipo_parametro_tppr ON
                        tppr_pk = prmt_tppr_pk
                    LEFT JOIN tbl_servicio_srvc ON
                        srvc_pk = srdt_srvc_dep_pk
            
        
            
            
                    AND EXISTS (
                        SELECT 1
                        FROM tbl_entidad_tipo_dato_entd
                        WHERE
                            entd_tpdt_pk = srdt_tpdt_pk
                            AND EXISTS (
                                SELECT 1
                                FROM tbl_servicio_srvc
                                WHERE srvc_tpsr_pk = entd_enti_pk
                                    AND srvc_pk = srdt_srvc_pk
                            )
                            AND entd_gridable = 1
                    )
            
            
            
                    AND EXISTS (
                        SELECT 1
                        FROM tbl_servicio_srvc
                        WHERE
                            srvc_pk = srdt_srvc_pk
            
            
            
                            AND srdt_srvc_pk = #{id}
            
            

            
            
                            AND srdt_srvc_pk IN
            
                #{item}
                
            

            
            
                            AND srvc_tpsr_pk = #{entiId}
            
            
            
            
                            AND srvc_subp_pk = #{subpId}
            
            
            
            
                            AND srvc_anno = #{anno}
            
            
            
            
                            AND srvc_numero = #{numero}
            
            
            
            
                            AND srvc_estado = #{estado}
            
            
            
                
                    
                        
                            AND EXISTS (
                                SELECT 1 FROM tbl_servicio_dato_srdt
                                WHERE srdt_srvc_pk = srvc_pk
                                    AND srdt_tpdt_pk = #{itdt.tpdtId}
                        
                        
                    
                                    AND srdt_prmt_pk = #{itdt.prmtId}
                    
                        
                        
                    
                                    AND srdt_srvc_dep_pk = #{itdt.srvcId}
                    
                        
                        
                    
                                    AND srdt_nentero = #{itdt.cantidadEntera}
                    
                        
                        
                    
                                    AND srdt_ndecimal = #{itdt.cantidadDecimal}
                    
                        
                        
                    
                                    AND srdt_cadena LIKE #{itdt.cadena}
                    
                        
                        
                    
                            )
                    
                    
                
            
        
        
                    )
            ) sql

mapper.insert:

INSERT INTO tbl_servicio_dato_srdt (
    srdt_srvc_pk, srdt_tpdt_pk, srdt_nentero, srdt_ndecimal
    , srdt_fecha, srdt_prmt_pk, srdt_cadena, srdt_srvc_dep_pk
) VALUES (
    #{itemId}, #{tpdtId}, #{cantidadEntera}, #{cantidadDecimal}
    , #{fecha}, #{prmt.id}, #{cadena}, #{srvc.id}
)

mapper.update:

UPDATE tbl_servicio_dato_srdt SET
    srdt_nentero = #{cantidadEntera}
    , srdt_ndecimal = #{cantidadDecimal}
    , srdt_fecha = #{fecha}
    , srdt_prmt_pk = #{prmt.id}
    , srdt_cadena = #{cadena}
    , srdt_srvc_dep_pk = #{srvc.id}
WHERE
	srdt_srvc_pk = #{itemId}
	AND srdt_tpdt_pk = #{tpdtId}
