mapper.update:
        
		UPDATE tbl_subservicio_ssrv SET
		    ssrv_estado = 'B'
		WHERE
			(
				(ssrv_tpss_pk = portico.getEntidad('BL') and ssrv_estado IN ('S', 'I', 'C'))
				OR (ssrv_tpss_pk = portico.getEntidad('PARTIDA') and ssrv_estado IN ('R'))
				OR (ssrv_tpss_pk = portico.getEntidad('EQUIPAMIENTO') and ssrv_estado IN ('R'))
			)
        
        
        
			AND EXISTS (
				SELECT 1
				FROM tbl_subserv_subserv_ssss
				WHERE
					ssss_ssrvh_pk = ssrv_pk
					AND ssss_ssrvp_pk = #{padreId}
			)
        
        
        
            AND ssrv_tpss_pk = #{entiId}
        
        
            AND ssrv_pk = #{id}
        
        
            
                AND ssrv_srvc_pk = #{srvc.id}

        
		UPDATE tbl_subservicio_ssrv SET
		    ssrv_estado = 'C'
		WHERE
			(
				(ssrv_tpss_pk = portico.getEntidad('BL') and ssrv_estado IN ('I'))
			)
        
        
        
			AND EXISTS (
				SELECT 1
				FROM tbl_subserv_subserv_ssss
				WHERE
					ssss_ssrvh_pk = ssrv_pk
					AND ssss_ssrvp_pk = #{padreId}
			)
        
        
        
            AND ssrv_tpss_pk = #{entiId}
        
        
            AND ssrv_pk = #{id}
        
        
            
                AND ssrv_srvc_pk = #{srvc.id}

UPDATE tbl_subservicio_ssrv SET ssrv_estado = CASE WHEN ssrv_tpss_pk = portico.getEntidad('BL') THEN 'I'
ELSE 'R' END WHERE ( (ssrv_tpss_pk = portico.getEntidad('BL') and ssrv_estado IN ('C', 'B')) OR (ssrv_tpss_pk = portico.getEntidad('PARTIDA') and ssrv_estado IN ('B')) OR (ssrv_tpss_pk = portico.getEntidad('EQUIPAMIENTO') and ssrv_estado IN ('B')) )
AND EXISTS ( SELECT 1 FROM tbl_subserv_subserv_ssss WHERE ssss_ssrvh_pk = ssrv_pk AND ssss_ssrvp_pk = #{padreId} )
AND ssrv_tpss_pk = #{entiId}
AND ssrv_pk = #{id}
AND ssrv_srvc_pk = #{srvc.id}

        
		UPDATE tbl_subservicio_ssrv SET
		    ssrv_estado = 'A'
		WHERE
			(
				(ssrv_tpss_pk = portico.getEntidad('BL') and ssrv_estado IN ('S', 'I', 'C'))
				OR (ssrv_tpss_pk = portico.getEntidad('PARTIDA') and ssrv_estado IN ('R'))
				OR (ssrv_tpss_pk = portico.getEntidad('EQUIPAMIENTO') and ssrv_estado IN ('R'))
			)
        
        
        
			AND EXISTS (
				SELECT 1
				FROM tbl_subserv_subserv_ssss
				WHERE
					ssss_ssrvh_pk = ssrv_pk
					AND ssss_ssrvp_pk = #{padreId}
			)
        
        
        
            AND ssrv_tpss_pk = #{entiId}
        
        
            AND ssrv_pk = #{id}
        
        
            
                AND ssrv_srvc_pk = #{srvc.id}
