SELECT estd_subp_pk, ZONA_DEP, UNID_SUP, TIPO_MERC_EST, TIPO_OP_SUP, ORGA, BOOLEANO_01, SUM(dias * ENTERO_01) AS ENTERO_01 FROM ( SELECT srvc_pk, srvc_fref, ssrv_pk, ssrv_fini, ssrv_ffin , EXTRACT (EPOCH FROM (ssrv_ffin - ssrv_fini)) / (60*60*24) AS dias , ( SELECT prdt_prmt_pk FROM tbl_parametro_dato_prdt WHERE prdt_tpdt_pk = portico.getTipoDato('AUT_PORT') AND prdt_prvr_pk = ANY ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref) AND prvr_prmt_pk = srvc_subp_pk ) ) AS estd_subp_pk , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('ZONA_DEP') ) AS ZONA_DEP , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('UNID_SUP') ) AS UNID_SUP , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('TIPO_MERC_EST') ) AS TIPO_MERC_EST , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('TIPO_OP_SUP') ) AS TIPO_OP_SUP , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('ORGA') ) AS ORGA , ( SELECT srdt_nentero FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('BOOLEANO_02') ) AS BOOLEANO_01 , ( SELECT ssdt_nentero FROM tbl_subservicio_dato_ssdt WHERE ssdt_ssrv_pk = ssrv_pk AND ssdt_tpdt_pk = portico.getTipoDato('ENTERO_02') ) AS ENTERO_01 FROM tbl_servicio_srvc INNER JOIN tbl_subservicio_ssrv ON ssrv_srvc_pk = srvc_pk WHERE srvc_tpsr_pk = portico.getEntidad('OCUPACION_SUPERFICIE') AND ssrv_tpss_pk = portico.getEntidad('OCUPACION_SUPERFICIE_LINEA')
