) ) AS TIPO_BUQUE_GT , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('TIPO_NAV') ) AS TIPO_NAV , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('TIPO_NAV_2') ) AS TIPO_NAV_2 , ( SELECT prdt_prmt_pk FROM tbl_parametro_dato_prdt WHERE prdt_tpdt_pk = portico.getTipoDato('PAIS') AND prdt_prvr_pk = ANY ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref) AND prvr_prmt_pk = BUQUE ) ) AS PAIS , ( SELECT srdt_cadena FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('TIPO_ESTAN_ESC') ) AS TIPO_ESTAN_ESC , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('SERV_TRAF') ) AS SERV_TRAF , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('ACUERDO') ) AS ACUERDO , ( SELECT ssdt_prmt_pk FROM tbl_subservicio_dato_ssdt WHERE ssdt_ssrv_pk = atraque_pk AND ssdt_tpdt_pk = portico.getTipoDato('ORGA_2') ) AS ORGA , ( SELECT prdt_nentero FROM tbl_parametro_dato_prdt WHERE prdt_tpdt_pk = portico.getTipoDato('ENTERO_01') AND prdt_prvr_pk = ANY ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref) AND prvr_prmt_pk = BUQUE ) ) AS ENTERO_02 FROM ( SELECT srvc_pk, srvc_subp_pk, srvc_fref, srvc_fini, srvc_ffin , ( SELECT srdt_prmt_pk FROM tbl_servicio_dato_srdt WHERE srdt_srvc_pk = srvc_pk AND srdt_tpdt_pk = portico.getTipoDato('BUQUE') ) AS BUQUE , ( SELECT MIN(ssrv_pk) FROM tbl_subservicio_ssrv WHERE ssrv_tpss_pk = portico.getEntidad('ATRAQUE') AND ssrv_estado = 'F' AND ssrv_srvc_pk = srvc_pk AND EXISTS ( SELECT 1 FROM tbl_subservicio_dato_ssdt WHERE ssdt_ssrv_pk = ssrv_pk AND ssdt_tpdt_pk = portico.getTipoDato('TIPO_ATR') AND ssdt_prmt_pk <> ( SELE