mapper.select:
SELECT esdt_estd_pk, esdt_tpdt_pk, esdt_nentero, esdt_ndecimal, esdt_prmt_pk, esdt_cadena , prmt_parametro
, ( CASE WHEN tppr_es_i18n = 1 THEN ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_pref = 'prvr' AND i18n_ext_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = esdt_prmt_pk AND pepr_freferencia BETWEEN prvr_fini AND COALESCE(prvr_ffin, pepr_freferencia) ) AND i18n_lang = 'es' ) WHEN tppr_tpdt_pk IS NOT NULL THEN ( SELECT prdt_cadena FROM tbl_parametro_dato_prdt WHERE prdt_prvr_pk = ANY ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = esdt_prmt_pk AND pepr_freferencia BETWEEN prvr_fini AND COALESCE(prvr_ffin, pepr_freferencia) ) AND esdt_tpdt_pk = tppr_tpdt_pk ) END ) AS p18n_texto
FROM ( SELECT * FROM tbl_estadistica_dato_esdt WHERE EXISTS ( SELECT 1 FROM tbl_estadistica_estd WHERE estd_pk = esdt_estd_pk
AND estd_pk = #{id}
AND estd_pk IN #{item}
AND EXISTS ( SELECT 1 FROM tbl_entidad_tipo_dato_entd WHERE entd_tpdt_pk = esdt_tpdt_pk AND entd_enti_pk = #{entiId} AND entd_gridable = 1 )
) ) sql INNER JOIN tbl_estadistica_estd ON estd_pk = esdt_estd_pk INNER JOIN tbl_periodo_proceso_pepr ON pepr_pk = estd_pepr_pk LEFT JOIN tbl_parametro_prmt ON prmt_pk = esdt_prmt_pk LEFT JOIN tbl_tipo_parametro_tppr ON tppr_pk = prmt_tppr_pk
