mapper.select:
SELECT vlrl_vlrc_pk, rgla_crgo_pk , vlrl_fref , (SELECT crgo_codigo FROM tbl_cargo_crgo WHERE crgo_pk = rgla_crgo_pk) AS crgo_codigo , SUM(vlrl_importe) AS vlrg_importe
, ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_pref = 'crgv' AND i18n_lang = #{idioma} AND i18n_ext_pk = ( SELECT crgv_pk FROM tbl_cargo_version_crgv WHERE crgv_crgo_pk = rgla_crgo_pk AND vlrl_fref BETWEEN crgv_fini AND COALESCE(crgv_ffin, vlrl_fref) ) ) AS crgv_descripcion
FROM ( SELECT vlrl_vlrc_pk , ( SELECT vlrc_fref FROM tbl_valoracion_vlrc WHERE vlrc_pk = vlrl_vlrc_pk ) AS vlrl_fref , ( SELECT rgla_crgo_pk FROM tbl_regla_rgla WHERE rgla_pk = vlrl_rgla_pk ) AS rgla_crgo_pk , ( SELECT SUM(vlrd_importe) FROM tbl_valoracion_det_vlrd WHERE vlrd_vlrl_pk = vlrl_pk ) AS vlrl_importe FROM tbl_valoracion_lin_vlrl
AND vlrl_vlrc_pk = #{id}
AND vlrl_vlrc_pk IN #{item}
) sql GROUP BY vlrl_vlrc_pk, vlrl_fref, rgla_crgo_pk
