                AND prdt_prvr_pk IN
                #{item}
                
            

        
            AND EXISTS (
                SELECT 1
                FROM tbl_parametro_version_prvr
                WHERE prvr_pk = prdt_prvr_pk
                    AND #{fechaVigencia} BETWEEN prvr_fini AND COALESCE(prvr_ffin, #{fechaVigencia})
        

            
                
                    
                    
                    
                    AND EXISTS (
                        SELECT 1
                        FROM tbl_parametro_dato_prdt
