                            AND prdt_ndecimal = #{itdt.cantidadDecimal}
                        
                        
                            AND prdt_cadena LIKE #{itdt.cadena}
                        
                        
                            AND prdt_cadena IN
                            #{cadena}
                            
                        
                        )
                    
                
            
            
        
