                        SELECT 1
                        FROM tbl_parametro_prmt
                        WHERE prmt_pk = prvr_prmt_pk
        
                
                    AND prmt_pk = #{id}
                
                
                    AND prmt_pk IN
                    #{item}
                    
                
                
                    AND prmt_tppr_pk = #{entiId}
                    
                
                    AND EXISTS (
                        SELECT 1
                        FROM tbl_entidad_tipo_dato_entd
                        WHERE
                            entd_tpdt_pk = prdt_tpdt_pk
