    , spdt_fecha
    , spdt_prmt_pk
    , spdt_cadena
) VALUES (
    #{itemId}
    , #{tpdtId}
    , #{cantidadEntera, jdbcType=BIGINT}
    , #{cantidadDecimal, jdbcType=DOUBLE}
    , #{fecha, jdbcType=TIMESTAMP}
    , #{prmt.id, jdbcType=BIGINT}
    , #{cadena, jdbcType=VARCHAR}
)

mapper.update:

    UPDATE tbl_subparametro_dato_spdt SET
