mapper.select:

SELECT COUNT(1)
FROM tbl_entidad_entidad_enen enen
WHERE
    enen.enen_entip_pk = #{entiPadreId}
    AND enen.enen_entih_pk = #{entiHijaId}

mapper.update:

UPDATE tbl_entidad_entidad_enen SET
    enen_orden = #{orden}
WHERE
    enen.enen_entip_pk = #{entiPadreId}
    AND enen.enen_entih_pk = #{entiHijaId}


DELETE FROM tbl_entidad_entidad_enen
WHERE
    enen.enen_entip_pk = #{entiPadreId}
    AND enen.enen_entih_pk = #{entiHijaId}
