mapper.select:

    SELECT COUNT(1)
    FROM
        tbl_tramite_trmt
    WHERE
        trmt_enti_pk = #{entiId}
        AND trmt_estado_orig = #{estadoOrig}
        AND trmt_estado_dest = #{estadoDest}

mapper.update:

    UPDATE tbl_tramite_trmt SET
    	trmt_estado_orig = #{estadoOrig}
    	, trmt_estado_dest = #{estadoDest}
    WHERE trmt_pk = #{id}
    	AND trmt_enti_pk = #{entiId}
