mapper.select:

    SELECT COUNT(1)
    FROM
        tbl_tramite_tipo_dato_trtd
    WHERE
        trtd_trmt_pk = #{trmtId}
    	AND trtd_tpdt_pk = #{entd.tpdt.id}

mapper.update:

    UPDATE tbl_tramite_tipo_dato_trtd SET
    	trtd_obligatorio = #{obligatorio}
    WHERE
        trtd_trmt_pk = #{trmtId}
    	AND trtd_tpdt_pk = #{entd.tpdt.id}

mapper.delete:

    DELETE FROM tbl_tramite_tipo_dato_trtd
    WHERE trtd_trmt_pk = #{trmtId}
    	AND trtd_tpdt_pk = #{entd.tpdt.id}
