mapper.select:

SELECT COUNT(1)
FROM tbl_servicio_srvc
WHERE srvc_tpsr_pk = #{entiId}
    AND srvc_subp_pk = #{prto.id}
    AND srvc_anno = #{anno}
    AND srvc_numero = #{numero}

select srvc_pk , srvc_anno , srvc_numero , prto_codigo , prto_codigo_corto from tbl_servicio_srvc JOIN tbl_puerto_prto ON prto_pk = srvc_subp_pk WHERE srvc_tpsr_pk = #{entiId}
AND prto_codigo_corto LIKE #{subpuerto}
AND srvc_anno LIKE #{anno}
AND srvc_numero LIKE #{numero}

mapper.insert:

