mapper.select:
SELECT srdt_srvc_pk, srdt_tpdt_pk, srdt_nentero, srdt_ndecimal, srdt_fecha, srdt_prmt_pk, srdt_srvc_dep_pk, srdt_cadena , srvc.srvc_fref AS srdt_fref , prmt_parametro , srvcDep.srvc_anno, srvcDep.srvc_numero , ( SELECT prmt_parametro FROM tbl_parametro_prmt WHERE prmt_pk = srvcDep.srvc_subp_pk ) AS srvc_subp
, ( CASE WHEN tppr_es_i18n = 1 THEN ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_pref = 'prvr' AND i18n_ext_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = srdt_prmt_pk AND srvc.srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc.srvc_fref) ) AND i18n_lang = 'es' ) WHEN tppr_tpdt_pk IS NOT NULL THEN ( SELECT prdt_cadena FROM tbl_parametro_dato_prdt WHERE prdt_prvr_pk = ANY ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = srdt_prmt_pk AND srvc.srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc.srvc_fref) ) AND prdt_tpdt_pk = tppr_tpdt_pk ) END ) AS p18n_texto
FROM tbl_servicio_dato_srdt INNER JOIN tbl_servicio_srvc srvc ON srvc.srvc_pk = srdt_srvc_pk LEFT JOIN tbl_parametro_prmt ON prmt_pk = srdt_prmt_pk LEFT JOIN tbl_tipo_parametro_tppr ON tppr_pk = prmt_tppr_pk LEFT JOIN tbl_servicio_srvc srvcDep ON srvcDep.srvc_pk = srdt_srvc_dep_pk WHERE EXISTS ( SELECT 1 FROM tbl_servicio_srvc WHERE srvc_pk = srdt_srvc_pk
AND srvc_pk = #{id}
AND srvc_pk IN #{item}
)
AND EXISTS ( SELECT 1 FROM tbl_entidad_tipo_dato_entd WHERE entd_tpdt_pk = srdt_tpdt_pk AND entd_enti_pk = #{entiId} AND entd_gridable = 1 )

mapper.insert:

INSERT INTO tbl_servicio_dato_srdt (
    srdt_srvc_pk, srdt_tpdt_pk, srdt_nentero, srdt_ndecimal
    , srdt_fecha, srdt_prmt_pk, srdt_cadena, srdt_srvc_dep_pk
) VALUES (
    #{itemId}, #{tpdtId}, #{cantidadEntera}, #{cantidadDecimal}
    , #{fecha}, #{prmt.id}, #{cadena}, #{srvc.id}
)

mapper.update:

UPDATE tbl_servicio_dato_srdt SET
    srdt_nentero = #{cantidadEntera}
    , srdt_ndecimal = #{cantidadDecimal}
    , srdt_fecha = #{fecha}
    , srdt_prmt_pk = #{prmt.id}
    , srdt_cadena = #{cadena}
    , srdt_srvc_dep_pk = #{srvc.id}
WHERE
	srdt_srvc_pk = #{itemId}
	AND srdt_tpdt_pk = #{tpdtId}
