mapper.select:
SELECT ssdt_ssrv_pk, ssdt_tpdt_pk, ssdt_nentero, ssdt_ndecimal, ssdt_fecha, ssdt_cadena, prmt_pk, prmt_parametro
, ( CASE WHEN tppr_es_i18n = 1 THEN ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_pref = 'prvr' AND i18n_ext_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = ssdt_prmt_pk AND srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref) ) AND i18n_lang = #{idioma} )
WHEN tppr_tpdt_pk IS NOT NULL THEN ( SELECT prdt_cadena FROM tbl_parametro_dato_prdt WHERE prdt_prvr_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = ssdt_prmt_pk AND srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, srvc_fref) ) AND prdt_tpdt_pk = tppr_tpdt_pk ) END ) AS p18n_texto
FROM ( SELECT * FROM tbl_subservicio_dato_ssdt WHERE EXISTS ( SELECT 1 FROM tbl_subservicio_ssrv WHERE ssrv_pk = ssdt_ssrv_pk
AND ssrv_pk = #{id}
AND ssrv_pk IN #{item}
AND EXISTS ( SELECT 1 FROM tbl_entidad_tipo_dato_entd WHERE entd_tpdt_pk = ssdt_tpdt_pk AND entd_enti_pk = #{entiId} AND entd_gridable = 1 )
) ) sql INNER JOIN tbl_subservicio_ssrv ON ssrv_pk = ssdt_ssrv_pk INNER JOIN tbl_servicio_srvc ON srvc_pk = ssrv_srvc_pk LEFT JOIN tbl_parametro_prmt ON prmt_pk = ssdt_prmt_pk LEFT JOIN tbl_tipo_parametro_tppr ON tppr_pk = prmt_tppr_pk

mapper.insert:

INSERT INTO tbl_subservicio_dato_ssdt (
    ssdt_ssrv_pk, ssdt_tpdt_pk, ssdt_nentero, ssdt_ndecimal
    , ssdt_fecha, ssdt_prmt_pk, ssdt_cadena
) VALUES (
    #{itemId}, #{tpdtId}, #{cantidadEntera}, #{cantidadDecimal}
    , #{fecha}, #{prmt.id}, #{cadena}
)


UPDATE tbl_subservicio_dato_ssdt SET
    ssdt_nentero = #{cantidadEntera}
    , ssdt_ndecimal = #{cantidadDecimal}
    , ssdt_fecha = #{fecha}
    , ssdt_prmt_pk = #{prmt.id}
    , ssdt_cadena = #{cadena}
WHERE
    ssdt_ssrv_pk = #{itemId}
    AND ssdt_tpdt_pk = #{tpdtId}
