mapper.select:
SELECT ssdt_ssrv_pk, ssdt_tpdt_pk, ssdt_nentero, ssdt_ndecimal, ssdt_prmt_pk, ssdt_fecha, ssdt_cadena
, prmt_parametro
, ( CASE WHEN EXISTS ( SELECT 1 FROM tbl_tipo_parametro_tppr WHERE tppr_pk = prmt_tppr_pk AND tppr_es_i18n = 1 ) THEN ( SELECT i18n_text FROM tbl_i18n_i18n WHERE i18n_lang = #{idioma} AND i18n_pref = 'prvr' AND i18n_ext_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = ssdt_prmt_pk AND sql.srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, sql.srvc_fref) ) )
ELSE ( SELECT prdt_cadena FROM tbl_parametro_dato_prdt WHERE prdt_prvr_pk = ( SELECT prvr_pk FROM tbl_parametro_version_prvr WHERE prvr_prmt_pk = ssdt_prmt_pk AND sql.srvc_fref BETWEEN prvr_fini AND COALESCE(prvr_ffin, sql.srvc_fref) ) AND prdt_tpdt_pk = ( SELECT tppr_tpdt_pk FROM tbl_tipo_parametro_tppr WHERE tppr_pk = prmt_tppr_pk ) ) END ) AS p18n_texto
FROM ( SELECT ssrv_pk , (SELECT srvc_fref FROM tbl_servicio_srvc WHERE srvc_pk = ssrv_srvc_pk) AS srvc_fref FROM tbl_subservicio_ssrv
AND ssrv_pk = #{id}
AND ssrv_pk IN #{item}
) sql INNER JOIN tbl_subservicio_dato_ssdt ON ssdt_ssrv_pk = sql.ssrv_pk
AND ssdt_tpdt_pk = ANY ( SELECT entd_tpdt_pk FROM tbl_entidad_tipo_dato_entd WHERE entd_enti_pk = #{entiId} AND entd_gridable = 1 )
LEFT JOIN tbl_parametro_prmt ON prmt_pk = ssdt_prmt_pk

mapper.insert:

INSERT INTO tbl_subservicio_dato_ssdt (
    ssdt_ssrv_pk, ssdt_tpdt_pk, ssdt_nentero, ssdt_ndecimal
    , ssdt_fecha, ssdt_prmt_pk, ssdt_cadena
) VALUES (
    #{itemId}, #{tpdtId}, #{cantidadEntera}, #{cantidadDecimal}
    , #{fecha}, #{prmt.id}, #{cadena}
)


UPDATE tbl_subservicio_dato_ssdt SET
    ssdt_nentero = #{cantidadEntera}
    , ssdt_ndecimal = #{cantidadDecimal}
    , ssdt_fecha = #{fecha}
    , ssdt_prmt_pk = #{prmt.id}
    , ssdt_cadena = #{cadena}
WHERE
    ssdt_ssrv_pk = #{itemId}
    AND ssdt_tpdt_pk = #{tpdtId}
